/** * 155B**/#include<bits/stdc++.h>#define ll long long#define testcase ll t;cin>>t;while(t--)#define pb push_back#define fi first#define se second#define vll vector<ll>#define for0(i, n) for (ll i = 0; i < (ll)(n); ++i)#define for1(i, n) for (ll i = 1; i <= (ll)(n); ++i)#define run ios_base::sync_with_stdio(false);cin.tie(NULL);cout.tie(NULL);#define endl '\n'using namespace std;bool sorter(pair<ll,ll> a, pair<ll,ll> b){    if(a.second > b.second)        return true;    if(a.second == b.second)        return (a.first>b.first);    return false;}int main(){    runll n;cin>>n;vector<pair<ll,ll>>v(n);for0(i,n) cin>>v[i].fi>>v[i].se;sort(v.begin(),v.end(),sorter);// for(auto x:v)// {//     cout<<x.fi<<" "<<x.se<<endl;// }if(n==1){    cout<<v[0].fi<<endl;    return 0;}ll ans=0;ll i=0;ll j=0;bool f=0;while(i <n && j<n && j>=0){    j = v[i].se;    ans +=v[i].fi;    while(j-- && i <n)    { //       cout<<i<<" "<<j<<endl;        i++;        if(i>=n)            break;        ans+=v[i].fi;        j +=v[i].se;    }    j--;    i++;}cout<<ans;    return 0;}